INSERT INTO `script_revisions` (`id`, `scriptid`, `revision`, `script`, `dtcreated`, `approval_state`) VALUES
(32, 2720, 1, '#!/bin/bash\n\nIPTABLES="/sbin/iptables"\n# Whitelisted IPs, separated by space\nIP_WHITELIST="%ip_whitelist%"\n\nSCALR_IPS=""\nFARM_IPS=""\n\nif [ ! -x $IPTABLES ]; then\n	echo "$IPTABLES not found." >&2\n\n	exit 1\nfi\n\nif [ -d /etc/scalr/private.d/hosts ]; then\n	DIRNAME="/etc/scalr/private.d/hosts"\nelif [ -d /etc/aws/hosts ]; then\n	DIRNAME="/etc/aws/hosts"\nelse\n	echo "Can''t find farm instances IP addresses in /etc/scalr/private.d/hosts or /etc/aws/hosts" >&2\n	echo "Not modifying firewall rules" >&2\n\n	exit 1\nfi\n\nfor FARM_IP in `find $DIRNAME -type f`; do\n	FARM_IP=`basename $FARM_IP`\n	FARM_IPS="$FARM_IPS $FARM_IP"\ndone\n\nif [ -z "$FARM_IPS" ]; then\n	echo "Can''t find farm instances IP addresses." >&2\n	echo "Not modifying firewall rules" >&2\n\n	exit 1\nfi\n\nif ! SCALR_IPS=`dig +short ip-pool.scalr.net` || [ -z "$SCALR_IPS" ]; then\n	echo "Can''t fetch Scalr IP addresses list. Check whether ''dig'' utility is installed." >&2\n	echo "Not modifying firewall rules" >&2\n\n	exit 1\nfi\n\n$IPTABLES -P INPUT ACCEPT\n\nif ! $IPTABLES -F FARM 2>/dev/null; then\n	$IPTABLES -N FARM\nfi\n\nfor IP2WHITELIST in $FARM_IPS; do\n	$IPTABLES -A FARM -p all -s $IP2WHITELIST -j ACCEPT\ndone\n\nif ! $IPTABLES -F SCALR 2>/dev/null; then\n	$IPTABLES -N SCALR\nfi\n\nfor IP2WHITELIST in $SCALR_IPS; do\n	$IPTABLES -A SCALR -p all -s $IP2WHITELIST -j ACCEPT\ndone\n\nif [ "$IP_WHITELIST" ]; then\n	for IP2WHITELIST in $IP_WHITELIST; do\n		$IPTABLES -A SCALR -p all -s $IP2WHITELIST -j ACCEPT \n	done\nfi\n\n$IPTABLES -D INPUT -i lo -j ACCEPT\n$IPTABLES -D INPUT -p ALL -m state --state ESTABLISHED,RELATED -j ACCEPT\n$IPTABLES -D INPUT -p ALL -j FARM\n$IPTABLES -D INPUT -p ALL -j SCALR\n\n$IPTABLES -I INPUT 1 -i lo -j ACCEPT\n$IPTABLES -I INPUT 2 -p ALL -m state --state ESTABLISHED,RELATED -j ACCEPT\n$IPTABLES -I INPUT 3 -p ALL -j FARM\n$IPTABLES -I INPUT 4 -p ALL -j SCALR\n$IPTABLES -P INPUT DROP', '2011-09-16 05:59:02', 'Approved'),
(30, 1845, 1, '#!/bin/bash\n\nkillall -9 burnP6 >/dev/null 2>&1\nkillall -9 burnMMX >/dev/null 2>&1\nkillall -9 burnP5 >/dev/null 2>&1\nkillall -9 burnBX >/dev/null 2>&1\nkillall -9 burnK7 >/dev/null 2>&1\nkillall -9 burnK6 >/dev/null 2>&1\n', '2011-04-26 20:40:24', 'Approved'),
(31, 2102, 1, '#!/bin/bash\n\nset -x\n\nif [ -f /usr/bin/yum ]; then\n    yum -y update scalarizr || :\n    yum -y update scalarizr-ec2 || :\n    yum -y update scalarizr-rackspace || :\nelse\n    apt-get update\n    export DEBIAN_FRONTEND=noninteractive\n    export DEBIAN_PRIORITY=critical\n\n    version=$(scalarizr -v 2>/dev/null | awk ''{ print $2 }'')\n    if dpkg --compare-versions "$version" lt ''0.7.0''; then\n        apt-get -y install scalarizr\n    else\n         platform=$(cat /etc/scalr/public.d/config.ini | grep ''platform\\s*='' | cut -d ''='' -f 2 | sed -e ''s/^\\s*//'' | sed -e ''s/\\s*$//'')\n         apt-get install -y scalarizr scalarizr-${platform}\n    fi\nfi', '2011-06-01 05:42:42', 'Approved'),
(29, 1844, 1, '#!/bin/bash\n\nif ! dpkg -s cpuburn >/dev/null 2>&1; then\n    apt-get install -y -y -q cpuburn\nfi\n\nif [ ! -x /usr/bin/burnP6 ]; then\n    echo "cpuburn cannot be installed on this instance." >&2\n\n    exit\nfi\n\n/usr/bin/burnP6 &\n/usr/bin/burnMMX &\n/usr/bin/burnP5 &\n/usr/bin/burnBX &\n/usr/bin/burnK7 &\n/usr/bin/burnK6 &\n', '2011-04-26 20:38:32', 'Approved'),
(25, 70, 1, '#!/bin/bash\necho "Enabling mod_rewrite..."\ntest -f "/etc/apache2/mods-available/rewrite.load" && ln -s /etc/apache2/mods-available/rewrite.load /etc/apache2/mods-enabled/rewrite.load\n\necho "Restarting Apache..."\n/etc/init.d/apache2 restart\n\n\n', '2009-01-10 16:03:11', 'Approved'),
(26, 71, 1, '#!/bin/bash\n\nif [ -s /etc/hosts ]; then\n        mv /etc/hosts /etc/hosts.save\nfi\n\ncat << EOLH > /etc/hosts\n127.0.0.1       localhost\n127.0.1.1       ubuntu\n\n# The following lines are desirable for IPv6 capable hosts\n::1     ip6-localhost ip6-loopback\nfe00::0 ip6-localnet\nff00::0 ip6-mcastprefix\nff02::1 ip6-allnodes\nff02::2 ip6-allrouters\nff02::3 ip6-allhosts\n\nEOLH\n\nfor ROLEDIR in /etc/aws/hosts/*; do\n        ROLE=`basename $ROLEDIR`\n\n        [ -z "$ROLE" ] && continue\n\n        for IPFILE in /etc/aws/hosts/$ROLE/*; do\n                IP=`basename $IPFILE`\n\n                [ -z "$IP" ] && continue\n\n                echo "$IP $ROLE int-$ROLE" >> /etc/hosts\n        done\ndone\n\n\n\n', '2009-01-12 16:34:26', 'Approved'),
(27, 80, 1, '#!/bin/bash\n\nif [ -r /usr/local/aws/lib/lib.sh ]; then\n        . /usr/local/aws/lib/lib.sh\nelse\n        echo "Error: /usr/local/aws/lib/lib.sh does not exist."\n\n        exit 1\nfi\n\nif [ -r /usr/local/aws/lib/nginxlib.sh ]; then\n        . /usr/local/aws/lib/nginxlib.sh\nelse\n        echo "Error: /usr/local/aws/lib/nginxlib.sh does not exist."\n\n        exit 1\nfi\n\nif [ -r /etc/aws/host.conf ]; then\n        . /etc/aws/host.conf\nelse\n        echo "Error: /etc/aws/host.conf does not exist."\n\n        exit 1\nfi\n\nif [ "$SERVER_ROLE" ] && [ "$SERVER_ROLE" = "www" ]; then\n        if type nginx_upstream_reload > /dev/null 2>/dev/null; then\n                nginx_upstream_reload\n        elif type reload_nginx_upstream > /dev/null 2>/dev/null; then\n                reload_nginx_upstream\n        else\n                echo "Error: nginx backend reconfiguration routine not defined."\n\n                exit 1\n        fi\nelse\n        echo "Error: not a www instance."\n\n        exit 1\nfi\n', '2009-01-16 08:51:16', 'Approved'),
(28, 90, 1, '#!/bin/bash\n\ntest -r /etc/aws/host.conf && . /etc/aws/host.conf\ntest -r /etc/aws/farmconfig && . /etc/aws/farmconfig\ntest -r /etc/aws/authconfig && . /etc/aws/authconfig\n\ntest -r /usr/local/aws/lib/lib.sh && . /usr/local/aws/lib/lib.sh\ntest -r /usr/local/aws/lib/mysqllib.sh && . /usr/local/aws/lib/mysqllib.sh\ntest -r /usr/local/aws/lib/s3lib.sh && . /usr/local/aws/lib/s3lib.sh\n\nMYSQLD_PID_FILE=`mysqld_get_param pid-file`\nMYSQLD_DATA_DIR=`mysqld_get_param datadir`\nBUCKET_NAME_MYSQL="$BUCKET_NAME/farm-mysql"\nMYSQL_CMD="/usr/bin/mysql --defaults-file=/etc/mysql/debian.cnf --batch"\nDATA_BUNDLE=""\n\nif lvm_enabled; then\n        . /etc/scalr_lvm.conf\nfi\n\n# Trying to lock\nif ! LPID=`mysql_data_bundle_lock`; then\n        loga "MySQL data bundle already in progress (PID: $LPID)."\n\n        exit 1\nfi\n\nlogq "Bundling MySQL data."\n\nif lvm_enabled; then\n        SNAP_DESTDIR="$LV_MISC_MPT/snapshot"\n        SNAP_SRCDIR="$LV_SNAP_MPT"\nelse\n        SNAP_DESTDIR=`mktemp -d -p /mnt`\n        SNAP_SRCDIR="/mnt/mysql-data-snapshot"\nfi\n\n[ -d $SNAP_DESTDIR ] || mkdir -p $SNAP_DESTDIR\n\nlogq "Locking tables and creating data snapshot."\n\n$MYSQL_CMD -e "FLUSH TABLES WITH READ LOCK; SYSTEM /usr/local/aws/bin/mysql-data-bundle.sh; UNLOCK TABLES"\n\nif [ -f $SNAP_SRCDIR/master-snap.conf ]; then\n        cp $SNAP_SRCDIR/master-snap.conf /etc/mysql/\n\n        if [ -r /etc/mysql/master-snap.conf ]; then\n                . /etc/mysql/master-snap.conf\n\n                if [ "$STATUS_USER" ] && [ "$STATUS_PASS" ]; then\n                        $MYSQL_CMD -e "GRANT REPLICATION CLIENT ON *.* TO ''$STATUS_USER''@''%'' identified by ''$STATUS_PASS''"\n                fi\n        fi\nelse\n        loga "Snapshot creation failed!"\n\n        if lvm_enabled; then\n                umount $LV_SNAP_MPT || echo -n\n                /sbin/lvremove -f /dev/$VG_NAME/$LV_SNAP_NAME >/dev/null 2>&1 || echo -n\n        fi\n\n        mysql_data_bundle_unlock\n\n        exit 1\nfi\n\n# InnoDB recovery\nlogq "Starting InnoDB recovery on the snapshot."\n\nif mysql_innodb_recover $SNAP_SRCDIR/mysql; then\n        logq "InnoDB recovery performed successfully."\nelse\n        loga "Failed to perform InnoDB recovery, snapshot corrupt!"\nfi\n\nlogq "Creating archive and splitting it into chunks."\n\n#\n# split snapshot into 150MB chunks (S3 allows files of up to 5G to be stored)\n#\ntar -cf - -C $SNAP_SRCDIR . | split -a 3 -b 150M - $SNAP_DESTDIR/mysql-snapshot.tar.\n\n# If RC!=0 or no files have been created\nif [ "$?" != "0" ] || ! ls $SNAP_DESTDIR/mysql-snapshot.tar.* >/dev/null 2>&1; then\n        loga "Failed to create snapshot."\n\n        mysql_data_bundle_unlock\n\n        exit 1\nfi\n\n[ -s $SNAP_DESTDIR/mysql-snapshot.index ] && rm $SNAP_DESTDIR/mysql-snapshot.index\n\n# Count snapshot chunks and their size\n\nSNAP_PARTS=0\nSNAP_SIZE=0\n\nfor CUR_VOLUME in $SNAP_DESTDIR/mysql-snapshot.tar.*; do\n        [ -r "$CUR_VOLUME" ] || continue\n\n        CHUNK_SIZE=`du -sm "$CUR_VOLUME" | awk ''{ print $1 }''`\n\n        SNAP_PARTS=$[ $SNAP_PARTS + 1 ]\n        SNAP_SIZE=$[ $SNAP_SIZE + $CHUNK_SIZE ]\n\n        echo "$CUR_VOLUME" >> $SNAP_DESTDIR/mysql-snapshot.index\ndone\n\nlogq "Snapshot created: $SNAP_PARTS chunks, total size: $SNAP_SIZE MB."\n\n[ -r $SNAP_DESTDIR/mysql-snapshot.index ] && DATA_BUNDLE="$SNAP_DESTDIR/mysql-snapshot.index"\n\nif lvm_enabled; then\n        logq "Unmounting LVM snapshot."\n\n        umount $LV_SNAP_MPT\n\n        /sbin/lvremove -f /dev/$VG_NAME/$LV_SNAP_NAME >/dev/null 2>&1\nelse\n        rm -r $SNAP_SRCDIR\nfi\n\nif [ -z "$DATA_BUNDLE" ] || [ ! -s "$DATA_BUNDLE" ]; then\n        loga "Failed to bundle MySQL data."\n        mysql_data_bundle_unlock\n\n        exit 1\nelse\n        log "Successfully bundled MySQL data to ''$DATA_BUNDLE''."\nfi\n\n# And upload it to S3\n\nif mysql_upload_bundle "$DATA_BUNDLE" "s3://$BUCKET_NAME_MYSQL"; then\n        SNAP_FNAME="`basename "$DATA_BUNDLE"`"\n        ec2_submit_event mysqlBckComplete "operation:bundle;snapurl:$BUCKET_NAME_MYSQL/$SNAP_FNAME"\n        log "Successfully uploaded MySQL data bundle to ''s3://$BUCKET_NAME_MYSQL/$SNAP_FNAME''."\n\n        mysql_delete_bundle "$DATA_BUNDLE"\n        mysql_data_bundle_unlock\nelse\n        loga "Failed to upload MySQL data bundle to S3."\n        mysql_data_bundle_unlock\n\n        exit 1\nfi\n', '2009-01-21 09:47:26', 'Approved'),
(21, 39, 1, '#!/bin/sh\n\ntouch /etc/aws/mysql_master_do_not_fetch_snapshot\n', '2008-12-13 08:31:20', 'Approved'),
(22, 50, 1, '#!/bin/bash\n\nPROMPT_FORMAT="%prompt_format%"\n\nif [ -z "$PROMPT_FORMAT" ]; then\n        PROMPT_FORMAT="\\u@@ROLE@[@IP@]:\\w\\\\\\$ "\nfi\n\nEXT_IP=`curl -s http://169.254.169.254/latest/meta-data/public-ipv4`\n\nif [ -f /etc/aws/host.conf ]; then\n        . /etc/aws/host.conf\n\n        if [ "$USER_ROLE" ]; then\n                ROLE="$USER_ROLE"\n        elif [ "$SERVER_ROLE" ]; then\n                ROLE="$SERVER_ROLE"\n        else\n                ROLE="unknown"\n        fi\n\n        if [ "$MYSQL_ROLE" ]; then\n                ROLE="$ROLE-$MYSQL_ROLE"\n        fi\nelse\n        ROLE="unknown"\nfi\n\nPROMPT_FORMAT=`echo "$PROMPT_FORMAT" | sed -e "s/@IP@/$EXT_IP/g" -e "s/@ROLE@/$ROLE/g" -e ''s/\\\\\\$/\\\\\\\\\\$/g''`\n\nif [ "$PROMPT_FORMAT" ]; then\n        echo "PS1=''$PROMPT_FORMAT''" >> /root/.bashrc\nfi\n', '2008-12-17 10:24:46', 'Approved'),
(23, 51, 1, '#!/bin/bash\n\nif [ -f /usr/local/aws/lib/lib.sh ]; then\n        . /usr/local/aws/lib/lib.sh\nelse\n        echo "/usr/local/aws/lib/lib.sh does not exist. Exiting."\n        exit\nfi\n\nif [ -d /etc/aws/hosts ]; then\n        rm -r /etc/aws/hosts\n\n        mkdir -p /etc/aws/hosts\nfi\n\n# Fill /etc/aws/hosts with farm hosts IPs\n\nMASTER_IP=`ec2_get_option_val db.mysql.master.ip`\n\nMYSQL_CUSTOM_ROLE=""\n\nfor HOST_ROLE in `ec2_listroles`; do\n        [ -d /etc/aws/hosts/$HOST_ROLE ] || mkdir -p /etc/aws/hosts/$HOST_ROLE\n\n        for HOST_IP in `ec2_listhosts $HOST_ROLE`; do\n                [ -f /etc/aws/hosts/$HOST_ROLE/$HOST_IP ] || touch /etc/aws/hosts/$HOST_ROLE/$HOST_IP\n\n                if [ "$HOST_ROLE" = "mysql" ]; then\n                        if [ "$MASTER_IP" != "$HOST_IP" ]; then\n                                [ -d /etc/aws/hosts/mysql-slave ]  || mkdir -p /etc/aws/hosts/mysql-slave\n                                touch /etc/aws/hosts/mysql-slave/$HOST_IP\n                        else\n                                [ -d /etc/aws/hosts/mysql-master ] || mkdir -p /etc/aws/hosts/mysql-master\n                                touch /etc/aws/hosts/mysql-master/$HOST_IP\n                        fi\n                fi\n\n                if [ "$MASTER_IP" ] && [ "$HOST_IP" == "$MASTER_IP" ] && [ "$HOST_ROLE" != "mysql" ]; then\n                        MYSQL_CUSTOM_ROLE="$HOST_ROLE"\n                fi\n        done\ndone\n\nif [ "$MYSQL_CUSTOM_ROLE" ]; then\n        for HOST_IP in `ec2_listhosts $MYSQL_CUSTOM_ROLE`; do\n                if [ "$HOST_IP" != "$MASTER_IP" ]; then\n                        [ -d /etc/aws/hosts/$MYSQL_CUSTOM_ROLE-slave  ] || mkdir -p /etc/aws/hosts/$MYSQL_CUSTOM_ROLE-slave\n                        touch /etc/aws/hosts/$MYSQL_CUSTOM_ROLE-slave/$HOST_IP\n                else\n                        [ -d /etc/aws/hosts/$MYSQL_CUSTOM_ROLE-master ] || mkdir -p /etc/aws/hosts/$MYSQL_CUSTOM_ROLE-master\n                        touch /etc/aws/hosts/$MYSQL_CUSTOM_ROLE-master/$HOST_IP\n                fi\n        done\nfi\n', '2008-12-19 05:47:25', 'Approved'),
(24, 51, 2, '#!/bin/bash\n\nif [ -f /usr/local/aws/lib/lib.sh ]; then\n        . /usr/local/aws/lib/lib.sh\nelse\n        echo "/usr/local/aws/lib/lib.sh does not exist. Exiting."\n        exit\nfi\n\nif [ -d /etc/aws/hosts ]; then\n        rm -r /etc/aws/hosts\n\n        mkdir -p /etc/aws/hosts\nfi\n\n# Fill /etc/aws/hosts with farm hosts IPs\n\nTMP_DIR=`mktemp -d`\nXMLSTARLET_PATH="/usr/bin/xmlstarlet"\n\nif [ ! -x $XMLSTARLET_PATH ]; then\n        loga "Cannot build /etc/aws/hosts: $XMLSTARLET_PATH not found."\n\n        exit 1\nfi\n\nif ! ec2_get_option_query_env list-roles > $TMP_DIR/roles.xml; then\n        loga "Cannot rertrieve farm hosts list from Scalr."\n\n        rm -r $TMP_DIR\nfi\n\nif [ ! -s $TMP_DIR/roles.xml ]; then\n        # logq "Farm hosts list empty."\n\n        exit 0\nfi\n\nif ! ROLES_COUNT=`$XMLSTARLET_PATH sel -t -v "count(/response/roles/role)" $TMP_DIR/roles.xml`; then\n        loga "Could not parse $TMP_DIR/roles.xml."\n\n        exit 1\nfi\n\nif [ -z "$ROLES_COUNT" ] || [ $ROLES_COUNT -lt 1 ]; then\n        # logq "Farm hosts list empty."\n\n        exit 0\nfi\n\nfor ROLE_ID in `seq 1 $ROLES_COUNT`; do\n        unset ROLE_NAME ROLE_BEHAV\n\n        ROLE_NAME=`$XMLSTARLET_PATH sel -t -v "/response/roles/role[$ROLE_ID]/@name" $TMP_DIR/roles.xml`\n        ROLE_BEHAV=`$XMLSTARLET_PATH sel -t -v "/response/roles/role[$ROLE_ID]/@behaviour" $TMP_DIR/roles.xml`\n\n        if [ -z "$ROLE_NAME" ] && [ -z "$ROLE_BEHAV" ]; then\n                continue\n        fi\n\n        HOSTS_COUNT=`$XMLSTARLET_PATH sel -t -v "count(/response/roles/role[$ROLE_ID]/hosts/host)" $TMP_DIR/roles.xml`\n\n        if [ -z "$HOSTS_COUNT" ] || [ $HOSTS_COUNT -lt 1 ]; then\n                continue\n        fi\n\n        for HOST_ID in `seq 1 $HOSTS_COUNT`; do\n                HOST_INT_IP=`$XMLSTARLET_PATH sel -t -v "/response/roles/role[$ROLE_ID]/hosts/host[$HOST_ID]/@internal-ip" $TMP_DIR/roles.xml`\n                HOST_REPL_MASTER=`$XMLSTARLET_PATH sel -t -v "/response/roles/role[$ROLE_ID]/hosts/host[$HOST_ID]/@replication-master" $TMP_DIR/roles.xml`\n\n                if [ "$HOST_INT_IP" ] && [ "$ROLE_NAME" ]; then\n                        [ -d /etc/aws/hosts/$ROLE_NAME ] || mkdir -p /etc/aws/hosts/$ROLE_NAME\n\n                        touch /etc/aws/hosts/$ROLE_NAME/$HOST_INT_IP\n                fi\n\n                if [ "$HOST_INT_IP" ] && [ "$ROLE_BEHAV" ]; then\n                        [ -d /etc/aws/hosts/$ROLE_BEHAV ] || mkdir -p /etc/aws/hosts/$ROLE_BEHAV\n\n                        touch /etc/aws/hosts/$ROLE_BEHAV/$HOST_INT_IP\n                fi\n\n                if [ "$HOST_REPL_MASTER" ] && [ $HOST_REPL_MASTER -eq 0 ]; then\n                        [ -d /etc/aws/hosts/$ROLE_NAME-slave ] || mkdir -p /etc/aws/hosts/$ROLE_NAME-slave\n                        [ -d /etc/aws/hosts/$ROLE_BEHAV-slave ] || mkdir -p /etc/aws/hosts/$ROLE_BEHAV-slave\n\n                        touch /etc/aws/hosts/$ROLE_NAME-slave/$HOST_INT_IP\n                        touch /etc/aws/hosts/$ROLE_BEHAV-slave/$HOST_INT_IP\n                fi\n\nif [ "$HOST_REPL_MASTER" ] && [ $HOST_REPL_MASTER -eq 1 ]; then\n                        [ -d /etc/aws/hosts/$ROLE_NAME-master ] || mkdir -p /etc/aws/hosts/$ROLE_NAME-master\n                        [ -d /etc/aws/hosts/$ROLE_BEHAV-master ] || mkdir -p /etc/aws/hosts/$ROLE_BEHAV-master\n\n                        touch /etc/aws/hosts/$ROLE_NAME-master/$HOST_INT_IP\n                        touch /etc/aws/hosts/$ROLE_BEHAV-master/$HOST_INT_IP\n                fi\n        done\ndone\n\nif [ -f $TMP_DIR/roles.xml ]; then\n        rm $TMP_DIR/roles.xml\nfi\n\nif [ -d $TMP_DIR ]; then\n        rmdir $TMP_DIR\nfi\n', '2010-01-13 13:02:14', 'Approved'),
(20, 4, 1, '#!/bin/bash\n\nGIT_PATH="/usr/bin/git"\nGIT_REPO_URL="%git_repo_url%"\nGIT_CL_DIR="%git_co_dir%"\n\n\nif [ -z "$GIT_REPO_URL" ]; then\n        echo "GIT repository URL was not specified." >&2\n\n        exit 1\nfi\n\nif [ -z "$GIT_CL_DIR" ]; then\n        echo "Destination directory was not specified." >&2\n\n        exit 1\nfi\n\nif [ ! -x "$GIT_PATH" ]; then\n        /usr/bin/apt-get -q -y install git-core\n\n        if [ ! -x "$GIT_PATH" ]; then\n                echo "GIT binary is not executable" >&2\n\n                exit 1\n        fi\nfi\n\n$GIT_PATH clone "$GIT_REPO_URL" "$GIT_CL_DIR"\n', '2008-12-02 07:03:36', 'Approved'),
(19, 3, 1, '#!/bin/bash\n\nSVN_PATH="/usr/bin/svn"\nSVN_REPO_URL="%svn_repo_url%"\nSVN_USER="%svn_user%"\nSVN_PASS="%svn_password%"\nSVN_REV="%svn_revision%"\nSVN_CO_DIR="%svn_co_dir%"\n\n\nif [ -z "$SVN_REPO_URL" ]; then\n        echo "SVN repository URL was not specified." >&2\n\n        exit 1\nfi\n\nif [ -z "$SVN_CO_DIR" ]; then\n        echo "Checkout directory was not specified." >&2\n\n        exit 1\nfi\n\nif [ -z "$SVN_PATH" ] || [ ! -x "$SVN_PATH" ]; then\n        echo "SVN binary is not executable" >&2\n\n        exit 1\nfi\n\n[ "$SVN_USER" ] && SVN_USER_STR="--username $SVN_USER"\n[ "$SVN_PASS" ] && SVN_PASS_STR="--password $SVN_PASS"\n[ "$SVN_REV" ]  && SVN_REV_STR="-r $SVN_REV"\n\n[ -d "$SVN_CO_DIR" ] || mkdir -p $SVN_CO_DIR\n\n$SVN_PATH --no-auth-cache --non-interactive $SVN_USER_STR $SVN_PASS_STR checkout $SVN_REV_STR "$SVN_REPO_URL" "$SVN_CO_DIR"\n', '2008-12-02 07:03:36', 'Approved'),
(17, 1, 1, '#!/bin/bash\n\nSVN_PATH="/usr/bin/svn"\nSVN_USER="%svn_user%"\nSVN_PASS="%svn_password%"\nSVN_REV="%svn_revision%"\nSVN_UP_DIR="%svn_co_dir%"\n\n\nif [ -z "$SVN_UP_DIR" ]; then\n        echo "Working copy directory was not specified." >&2\n\n        exit 1\nfi\n\nif [ -z "$SVN_PATH" ] || [ ! -x "$SVN_PATH" ]; then\n        echo "SVN binary is not executable" >&2\n\n        exit 1\nfi\n\n[ "$SVN_USER" ] && SVN_USER_STR="--username $SVN_USER"\n[ "$SVN_PASS" ] && SVN_PASS_STR="--password $SVN_PASS"\n[ "$SVN_REV" ]  && SVN_REV_STR="-r $SVN_REV"\n\n\nif $SVN_PATH --non-interactive --no-auth-cache $SVN_USER_STR $SVN_PASS_STR info "$SVN_UP_DIR" >/dev/null 2>&1; then\n        $SVN_PATH --non-interactive $SVN_USER_STR $SVN_PASS_STR $SVN_REV_STR update "$SVN_UP_DIR"\nfi\n', '2008-12-02 07:03:36', 'Approved'),
(18, 2, 1, '#!/bin/bash\n\nSVN_PATH="/usr/bin/svn"\nSVN_REPO_URL="%svn_repo_url%"\nSVN_USER="%svn_user%"\nSVN_PASS="%svn_password%"\nSVN_REV="%svn_revision%"\nSVN_CO_DIR="%svn_co_dir%"\n\n\nif [ -z "$SVN_REPO_URL" ]; then \n        echo "SVN repository URL was not specified." >&2\n\n        exit 1\nfi\n\nif [ -z "$SVN_CO_DIR" ]; then\n        echo "Checkout directory was not specified." >&2\n\n        exit 1\nfi\n\nif [ -z "$SVN_PATH" ] || [ ! -x "$SVN_PATH" ]; then\n        echo "SVN binary is not executable" >&2\n\n        exit 1\nfi\n\n[ "$SVN_USER" ] && SVN_USER_STR="--username $SVN_USER"\n[ "$SVN_PASS" ] && SVN_PASS_STR="--password $SVN_PASS"\n[ "$SVN_REV" ]  && SVN_REV_STR="-r $SVN_REV"\n\n[ -d "$SVN_CO_DIR" ] || mkdir -p $SVN_CO_DIR\n\n$SVN_PATH --force --non-interactive $SVN_USER_STR $SVN_PASS_STR export $SVN_REV_STR "$SVN_REPO_URL" "$SVN_CO_DIR"\n', '2008-12-02 07:03:36', 'Approved');
